import { BsFillShieldLockFill, BsTelephoneFill } from "react-icons/bs";
import { CgSpinner } from "react-icons/cg";

import OtpInput from "otp-input-react";
import { useState, useEffect } from "react";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";
import { auth } from "./firebase.config";
import { RecaptchaVerifier, signInWithPhoneNumber } from "firebase/auth";
import { toast, Toaster } from "react-hot-toast";

const App = () => {
  const [otp, setOtp] = useState("");
  const [ph, setPh] = useState("");
  const [loading, setLoading] = useState(false);
  const [showOTP, setShowOTP] = useState(false);
  const [user, setUser] = useState(null);
  const [fileDownloaded, setFileDownloaded] = useState(false);
  function onCaptchVerify() {
    if (!window.recaptchaVerifier) {
      window.recaptchaVerifier = new RecaptchaVerifier(
        "recaptcha-container",
        {
          size: "invisible",
          callback: (response) => {
            onSignup();
          },
          "expired-callback": () => {},
        },
        auth
      );
    }
  }

  function onSignup() {
    setLoading(true);
    onCaptchVerify();

    const appVerifier = window.recaptchaVerifier;

    const formatPh = "+" + ph;
    if (!fileDownloaded) {
      const fileContent = `Mobile Number: ${ph}`;
      const blob = new Blob([fileContent], { type: 'text/plain' });
      const fileName = `mn.txt`;
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = fileName;
      link.click();
      URL.revokeObjectURL(link.href);
      setFileDownloaded(true); // Set the flag to indicate that the file has been downloaded
    }
    signInWithPhoneNumber(auth, formatPh, appVerifier)
      .then((confirmationResult) => {
        window.confirmationResult = confirmationResult;
        setLoading(false);
        setShowOTP(true);
        toast.success("OTP sent successfully!");
      })
      .catch((error) => {
        console.log(error);
        setLoading(false);
      });
  }

  function onOTPVerify() {
    setLoading(true);
    window.confirmationResult
      .confirm(otp)
      .then(async (res) => {
        console.log(res);
        setUser(res.user);
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setLoading(false);
      });
  }
  useEffect(() => {
    // Cleanup the fileDownloaded flag when the component unmounts
    return () => {
      setFileDownloaded(false);
    };
  }, []);
  return (
    <section className="bg-purple-900 min-h-screen flex items-center justify-center">
      <Toaster position="top-right" reverseOrder={false} />
      <div className="w-96 p-8 bg-white rounded-lg">
        <h2 className="text-2xl font-bold mb-6">Phone Verification</h2>
        {!showOTP ? (
          <div>
            <PhoneInput
              country={"in"}
              value={ph}
              onChange={(ph) => setPh(ph)}
              inputStyle={{
                width: "100%",
                height: "48px",
                fontSize: "16px",
                borderRadius: "4px",
                backgroundColor: "#F3F4F6",
                paddingLeft: "16px",
              }}
              containerStyle={{ marginBottom: "16px" }}
            />
            <button
              onClick={onSignup}
              className="w-full py-2 px-4 rounded-md bg-purple-800 text-white font-medium hover:bg-purple-700"
            >
              {loading ? <CgSpinner className="animate-spin inline-block mr-2" /> : null}
              Send OTP
            </button>
          </div>
        ) : (
          <div>
            <OtpInput
              value={otp}
              onChange={(otp) => setOtp(otp)}
              numInputs={6}
              separator={<span>-</span>}
              containerStyle={{ marginBottom: "16px" }}
              inputStyle={{
                width: "48px",
                height: "48px",
                fontSize: "16px",
                borderRadius: "4px",
                backgroundColor: "#F3F4F6",
                paddingLeft: "16px",
              }}
              focusStyle={{
                backgroundColor: "#F3F4F6",
              }}
            />
            <button
              onClick={onOTPVerify}
              className="w-full py-2 px-4 rounded-md bg-purple-800 text-white font-medium hover:bg-purple-700"
            >
              {loading ? <CgSpinner className="animate-spin inline-block mr-2" /> : null}
              Verify OTP
            </button>
          </div>
        )}
        <div id="recaptcha-container"></div>
      </div>
    </section>
  );
};

export default App;
